import React from "react";
import {
  Redirect,
  Link,
  Switch,
  BrowserRouter,
  Route,
  useHistory,
  useLocation,
} from "react-router-dom";
import LoginForm from "./LoginForm";
import RegisterForm from "./RegisterForm";

const users = [];

// '/register' path를 추가하세요.
export default function UserLogin() {
  return (
    <BrowserRouter>
      <Switch>
        <Route exact path="/">
          <HomePage />
        </Route>

        <Route exact path="/login">
          <LoginPage />
        </Route>

        <Route exact path="/detail">
          <UserDetailPage />
        </Route>

        <Route exact path ='/register'>
          <RegisterPage />
        </Route>
      </Switch>
    </BrowserRouter>
  );
}

function HomePage() {
  return (
    <div>
      <h2>Welcome to my homepage.</h2>
      <div>
        <Link to="/login">Login</Link>
      </div>
    </div>
  );
}

// users 리스트에 등록된 회원인 경우에만 회원의 상세한 정보를 띄우는 코드를 작성하세요.
function LoginPage() {
  const history = useHistory();
  const handleSubmit = (formData) => {
    const foundUser = users.find(user => user.email === formData.email && user.password ===formData.password)

    if (!foundUser) return
    history.push(`/detail?email=${formData.email}&password=${formData.password}`)
    // console.log(formData)
  }
  
  return (
    <div>
      <h2>Login Page</h2>
      <LoginForm onSubmit={handleSubmit} />
      <div>
        <ul>
          <li>
            <Link to="/">Back to home</Link>
          </li>

          <li>
            <Link to="/register">Register</Link>
          </li>
        </ul>
      </div>
    </div>
  );
}

function UserDetailPage() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  const email = searchParams.get("email");
  const password = searchParams.get("password");

  if (!email || !password) {
    return <Redirect to="/login" />;
  }

  return (
    <div>
      <h2>User Detail Page</h2>
      <p>
        <h3>User details</h3>
        <em>{email}</em>
        <br />
        <strong>{password}</strong>
      </p>
      <Link to="/">Logout</Link>
    </div>
  );
}

// '/register'로 이동 시 RegisterForm으로 렌더링하는 코드를 작성하세요.
function RegisterPage() {
  const history = useHistory();

  const handleSubmit = (formData) => {
      users.push(formData)
      history.push('/login')
      console.log('유저를 등록하세요.')
  };

  return (
    <div>
      <h2>Register Page</h2>
      <RegisterForm onSubmit={handleSubmit} />
      <div>
        <ul>
          <li>
            <Link to="/">Back to home</Link>
          </li>

          <li>
            <Link to="/login">Login</Link>
          </li>
        </ul>
      </div>
    </div>
  );
}
